import 'angular-material/angular-material.css';
import 'angular-material-data-table/dist/md-data-table.css';
import 'ng-material-floating-button/mfb/dist/mfb.css';
import 'mdi/css/materialdesignicons.css';
import 'sweetalert2/dist/sweetalert2.css';

import angular from 'angular';
import angularAnimate from 'angular-animate';
import angularUiRouter from 'angular-ui-router';
import 'angular-ui-router/release/stateEvents.js';
import angularMaterial from 'angular-material';
import translate from 'angular-translate';
import ngStorage from 'ngstorage';
import { ngSweetAlert2 } from 'angular-h-sweetalert';
import 'angular-file-upload';
import 'angular-truncate-2';
import 'angular-translate-loader-static-files';
import 'angular-ui-sortable/dist/sortable.min';
import 'ng-material-floating-button/src/mfb-directive';
import 'material-angular-paging/build/dist.min';
import 'angular-material-data-table';
import 'angular-messages';

import coreModule from './core/core';
import cardsModule from './cards/cards';
import departmentsModule from './departments/departments';
import vehiclesModule from './vehicles/vehicles';
import usersModule from './users/users';
import cardUsageModule from './cards/card-usage/card-usage';
import recordsModule from './records/records';
import 'moment';

import englishTranslations from './languages/en.json';
import spanishTranslations from './languages/es.json';

// Define constants
export const appModule = 'app';

const NODE_ENV = process.env.NODE_ENV || 'development';
const API = process.env.API;

// Initialize application module
angular.module(appModule, [
  // Inject dependencies
  angularUiRouter,
  angularAnimate,
  'ngMessages',
  'angularFileUpload',
  angularMaterial,
  'md.data.table',
  ngStorage.name,
  translate,
  ngSweetAlert2,
  'ui.sortable',
  'ng-mfb',
  'cl.paging',
  'truncate',
  'ui.router.state.events',
  coreModule,
  cardsModule,
  departmentsModule,
  vehiclesModule,
  usersModule,
  cardUsageModule,
  recordsModule,
])// Config translations
  .config(($translateProvider) => {
  $translateProvider.translations('en', englishTranslations);
  $translateProvider.translations('es', spanishTranslations);
  $translateProvider.useSanitizeValueStrategy(null);
  $translateProvider.preferredLanguage('en');
})// Config material design theming
  .config(($mdIconProvider, $mdThemingProvider) => {
    $mdIconProvider
      .fontSet('mdi', 'material-icons');

    let greenMap = $mdThemingProvider.extendPalette('green', {
      'contrastDefaultColor': 'light',
    });
    $mdThemingProvider.definePalette('newGreen', greenMap);

    let redMap = $mdThemingProvider.extendPalette('red', {
      'contrastDefaultColor': 'light',
    });
    $mdThemingProvider.definePalette('newRed', redMap);

    $mdThemingProvider.theme('default')
      .primaryPalette('newGreen')
      .warnPalette('newRed');
  })
  // Add constants in angular context
  .constant('NODE_ENV', NODE_ENV)
  .constant('API', API)
  .run(($rootScope) => {
    $rootScope.stateIsLoading = false;

    $rootScope.$on('$stateChangeStart',() => {
      $rootScope.stateIsLoading = true;
    });

    $rootScope.$on('$stateChangeSuccess', () => {
      $rootScope.stateIsLoading = false;
    });
  });

import moment from 'moment';

/**
 * CardsUsage Controller is in charge of presentation and validation logic
 * for usage related states and interactions.
 */
export default class CardsUsageController {
  /**
   * Constructs a new instance of CardsUsageController and initializes it.
   * @param $state
   * @param $log
   * @param $stateParams
   * @param $window
   * @param FileUploader
   * @param AuthService
   * @param CardsService
   * @param CardUsageService
   * @param swal
   * @param API
   */
  constructor($state, $log, $stateParams, $window, FileUploader,
              AuthService, CardsService, CardUsageService, swal, API) {
    this.$state = $state;
    this.$log = $log;
    this.$stateParams = $stateParams;
    this.$window = $window;
    this.authService = AuthService;
    this.cardUsageService = CardUsageService;

    this.cardsService = CardsService;
    this.cardsUsages = this.cardUsageService.cardsUsages;
    this.singleCardUsages = this.cardUsageService.singleCardUsages;
    this.temp_date = new Date();
    this.swal = swal;
    this.API = API;

    this.card = this.cardsService.getCard($stateParams.id);

    this.pageQuery = {
      page: this.cardUsageService.page,
    };

    this.pagination = {
      boundaryLinks: true,
      limit: this.cardUsageService.pageSize,
      total: this.cardUsageService.total,
    };

    this.getPaginatedSingleCardUsages = this.getPaginatedSingleCardUsages.bind(this);

    this.newCardUsage = {
      date: '',
      vehicle_mileage: '',
      provider_number: '',
      purchase_type: '',
      total_liters: '',
      total_receipt: '',
      receipt_number: '',
      comments: '',
      vehicle_id: '',
      card_id: '',
      custodian_id: '',
      department_id: ''
    };
    this.onDateChange();

    this.date = new Date();

    this.today = new Date(
      this.date.getFullYear(),
      this.date.getMonth(),
      this.date.getDate()
    );

    /**
     * Uploader object used for uploading assets like
     * receipt images and travel ballots.
     */
    this.uploader = new FileUploader({
      url: this.API + '/api/v1/records',
      alias: 'filename',
      method: 'POST',
      headers: {
        Authorization: 'Bearer ' + this.authService.getToken(),
      },
    });

    /**
     * Gathers new Card Usage data and file (receipt picture),
     * aggregates them to the form data.
     * @param {File} fileItem File item to be processed before upload
     * @callback
     */
    this.uploader.onBeforeUploadItem = (fileItem) => {
      this.newCardUsage.custodian_id = this.authService.getUser().id;
      this.newCardUsage.card_id = this.cardsService.card.id;
      this.newCardUsage.department_id = this.cardsService.card.department_id;
      this.newCardUsage.vehicle_id = 1;
      fileItem.formData.push(this.newCardUsage);
    };

    /**
     * Once upload is complete, navigate back to card usage state.
     * @param item
     * @param response
     * @param status
     * @param headers
     * @callback
     */
    this.uploader.onCompleteItem = (item, response, status, headers) => {
      this.$state.go('dashboard.cards.view.card-usage');
    };

    this.tempItem = null;
  }

  /**
   * Shows a card usage receipt in a new tab
   * @param {Object} usage Usage object for receipt display
   */
  showReceipt(usage){
    this.$window.open(usage.record_picture, "_blank");
  }

  /**
   * Shows confirmation dialog to user
   * If user confirms, a new card usage/record will be created
   * If user cancels, he/she will stay in the "Add New Card Usage" view
   */
  confirmUsageForm() {

    this.swal({
      title: 'Confirm transaction',
      html: '<div class="confirmation-table">' +
      '<ul style="list-style-type:none; text-align: left">' +
      '<li> Date:  ' + '<b>' + moment(this.newCardUsage.date).format('MMM D, YYYY') + '</b></li>' +
      '<li> Store:  ' + '<b>' + this.newCardUsage.provider_number + '</b></li>' +
      '<li> Vehicle mileage:  ' + '<b>' + this.newCardUsage.vehicle_mileage + '</b></li>' +
      '<li> Purchase type:  ' + '<b>' + this.newCardUsage.purchase_type + '</b></li>' +
      '<li> Total of liters:  ' + '<b>' + this.newCardUsage.total_liters + '</b></li>' +
      '<li> Receipt total:  ' + '<b>' + this.newCardUsage.total_receipt + '</b></li>' +
      '<li> Receipt number: ' + '<b>' + this.newCardUsage.receipt_number + '</b></li>' +
      '<li> Comments:  ' + '<b>' + this.newCardUsage.comments + '</b></li></ul></div>',
      type: 'info',
      confirmButtonText: 'Confirm',
      confirmButtonColor: '#4caf50',
      showCancelButton: 'true',
      cancelButtonText: 'Cancel',
      cancelButtonColor: '#f44336',
    }).then(() => {

      this.submitUsageForm();

        if(this.uploader.queue.length === 0){
          this.swal({
            title: 'Receipt not found!',
            text: 'You need to submit a receipt with the transaction.',
            type: 'info',
          });
        }
        else{
          this.swal({
            title: 'Success!',
            text: 'You have submitted a new transaction successfully',
            type: 'success'
          });
        }
      });
  }

  /**
   * Sends newly created gas transaction to backend
   */
  submitUsageForm(){

    for (let x = 0; x < this.uploader.queue.length; x++){
      this.tempItem = this.uploader.queue[x];
    }

    if(this.uploader.queue.length !== 0) {
      this.tempItem.upload();
    }
  }

  /**
   * Method used to format date in the format YYYY-MM-DD
   */
  onDateChange() {
    this.newCardUsage.date = moment(this.temp_date).format('YYYY-M-D');
  }

  /**
   * Requests card usages to service with filter object provided.
   * @return {Promise<Object>}
   */
  getCardsUsages() {
    return this.cardUsageService.getCardsUsages();
  }

  /**
   * Requests card usages of a single card to service with id provided.
   * @param {number} id Numerical value assigned by API
   * @return {Promise<Object>}
   */
  getSingleCardUsages(id) {
    return this.cardUsageService.getSingleCardUsages(id);
  }

  /**
   * Requests modification of a specific card usage to service,
   * with id of desired
   * @param {number} id Numerical value assigned by API
   * @return {Promise<Object>}
   */
  editCardUsage(id) {
    return this.cardUsageService.editCardUsage(id)
      .then(() => {
        this.$state.go('dashboard.cards.view.card-usage');
      });
  }

  /**
   * Requests card usages of a single card to service with id provided,
   * paginated for presentation purposes.
   * @return {Promise<Object>}
   */
  getPaginatedSingleCardUsages(){
    return this.cardUsageService.getSingleCardUsages(this.$stateParams.id, this.pageQuery)
      .then( () => {
        this.singleCardUsages = this.cardUsageService.singleCardUsages;
      });
  }

  /**
   * Gets currently logged user
   * @returns {Object|null}
   */
  getUser() {
    return this.authService.getUser();
  }
}

import cardsUsageController from './card-usage.controller';
import cardUsageService from './card-usage.service';

import cardUsageTemplate from './card-usage.html';
import cardUsageFormTemplate from './views/add-card-usage.html';

const cardUsageModule = angular.module('app.cards.card-usage', [])
  .config(function ($stateProvider) {

    $stateProvider
      .state('dashboard.cards.view.card-usage', {
        url: '/card-usage',
        template: cardUsageTemplate,
        controller: 'cardsUsageCtrl as ctrl',
        resolve: {
          singleCardUsages: ($stateParams,CardUsageService) =>
          CardUsageService.getSingleCardUsages($stateParams.id),
          card: ($stateParams, CardsService) => CardsService.getCard($stateParams.id),
        },
      })
      .state('dashboard.cards.view.add-usage', {
        url: '/card-usage/add',
        template: cardUsageFormTemplate,
        controller: 'cardsUsageCtrl as ctrl',
        resolve: {
          card: ($stateParams, CardsService) => CardsService.getCard($stateParams.id),
        },
      })

  })
  .service('CardUsageService', cardUsageService)
  .controller('cardsUsageCtrl', cardsUsageController)
  .name;

/**
 * CardUsage module is in charge of encapsulating all functionality
 * related to card expenses inside the application.
 * This angular module declares all states from the card-usage
 * sub tree of views.
 * @type {string}
 * @return {string} 'app.cards.card-usage'
 */
export default cardUsageModule;

/**
 * CardUsage Service is in charge of emitting http requests to the API
 * related to card expense information.
 */
export default class CardUsageService {
  /**
   * Constructs a new instance of CardUsageService and initializes it.
   * @param $http
   * @param $log
   * @param API
   */
  constructor($http, $log, API) {
    this.$http = $http;
    this.$log = $log;
    this.API = API;
    this.resource = '/api/v1/records/';

    // Initialize all cards usage list
    this.cardsUsages = [];

    //Initialize single card usage
    this.singleCardUsages = [];

    // Initialize pagination metadata
    this.pageSize = 10;
    this.total = 1;
    this.page = 1;
  }

  /**
   * Requests usages to the API, filtering results if params provided.
   * @param {Object} params Object where each key and value is used
   * for filtering requested Usage objects
   * @return {Promise<Object>}
   */
  getCardsUsages(params) {
    return this.$http.get(this.API + this.resource, {
      params: params
    }).then((response) => {
        this.$log.log(response);
        this.cardsUsages = response.data.data[0].data;
        this.pageSize = response.data.data[0].per_page;
        this.total = response.data.data[0].last_page;
        return this.cardsUsages;
      })
      .catch((error) => {
        this.$log.log(error);
      });
  };

  /**
   * Requests a single card usage from a card to the API.
   * @param {number} cardID Numerical value provided by the API
   * @param {Object} params
   * @return {Promise<Object>}
   */
  getSingleCardUsages(cardID, params) {
    //Empty array of single card usages
    this.singleCardUsages = [];
    return this.$http.get(this.API + this.resource + 'card/' + cardID, {
      params: params
    })
      .then((response) => {
      this.$log.log(response);
      this.singleCardUsages = response.data.data[0].data;
      this.pageSize = response.data.data[0].per_page;
      this.total = response.data.data[0].total;
      return this.singleCardUsages;
    })
      .catch((error) => {
        this.$log.log(error);
      });
  };

  /**
   * Requests the deletion of a single existing Card Usage
   * object to the API.
   * @param {number} id Numerical id of Usage object for deletion
   * @returns {Promise<Object>}
   */
  deleteCardUsage(id) {
    return this.$http.delete(this.API + this.resource + id);
  };

  /**
   * Requests the modification of a single existing
   * Usage object to the API.
   * @param {Object} cardUsage Usage for modification
   * @returns {Promise<Object>}
   */
  editCardUsage(cardUsage) {
    return this.$http.put(this.API + this.resource + cardUsage);
  };

}

/**
 * Cards Controller is in charge of presentation and validation logic
 * for card related states and interactions.
 */
export default class CardsController {
  /**
   * Constructs a new instance of CardsController and initializes it.
   * @param $state
   * @param $log
   * @param AuthService
   * @param CardsService
   * @param UsersService
   * @param DepartmentsService
   * @param swal
   */
  constructor($state, $log, AuthService, CardsService,
              UsersService, DepartmentsService, swal) {
    this.$state = $state;
    this.$log = $log;
    this.authService = AuthService;
    this.cardsService = CardsService;
    this.usersService = UsersService;
    this.departmentsService = DepartmentsService;
    this.swal = swal;
    this.card = this.cardsService.card;
    this.cards = this.cardsService.cards;
    this.departments = this.departmentsService.departments;
    this.newCard = {};
    this.nonPaginatedUsers = this.usersService.nonPaginatedUsers;

    this.cardTypeOptions = ["Regular", "Premium", "Diesel", "Spare"];
    this.cardStatusOptions = ["Active", "Inactive"];

    this.filter = {
      department_id: '',
      custodian_id: '',
      type: '',
      status: 'Active',
      page: 1,
    };

    this.pagination = {
      pageSize: this.cardsService.pageSize,
      total: this.cardsService.total,
    };
  }

  /**
   * Asks for user confirmation before actually processing creation.
   */
  confirmCardCreation(){
    this.createCard()
      // If card creation is successful, shows user success feedback
      .then(() => {
        this.swal({
          title: 'Card was successfully created!',
          type: 'success'
        });
      })
      // If card creation is unsuccessful, shows user error feedback
      .catch((error) => {
        this.swal({
          title: 'Error: Card could not be created',
          type: 'error'
        });
      });
  }

  /**
   * Requests card creation to service, providing the Card object.
   * @return {Promise<Object>}
   */
  createCard() {
    return this.cardsService.createCard(this.newCard)
      .then(() => {
      this.$state.go('dashboard.cards.list');
    });
  }

  /**
   * Asks for user confirmation before actually processing deletion.
   * @param {Object} card Card to be deleted
   */
  confirmCardDeletion(card){

    this.swal({
      title: 'Do you really want to delete this card?',
      type: 'warning',
      confirmButtonText: 'Confirm',
      confirmButtonColor: '#4caf50',
      showCancelButton: 'true',
      cancelButtonText: 'Cancel',
      cancelButtonColor: '#f44336',
    }).then(() => {

      this.deleteCard(card)
        .then(() => {
          this.swal({
            title: 'Card has been deleted',
            type: 'success'
          });
        })
        .catch((error) => {
          this.swal({
            title: 'Error: Card could not be deleted',
            type: 'error'
          });
        });
    });
  }

  /**
   * Requests card deletion to service, providing
   * desired Card object.
   * @return {Promise<Object>}
   */
  deleteCard(card) {
    return this.cardsService.deleteCard(card)
      .then(() => {
      this.$state.reload();
    });
  }

  /**
   * If card edition is successful, shows user success feedback
   * If card edition is unsuccessful, shows user error feedback
   */
  confirmCardEdition(){

      this.editCard()
        .then(() => {
          this.swal({
            title: 'Card was successfully edited!',
            type: 'success'
          });
        })
        .catch((error) => {
          this.swal({
            title: 'Error: Card could not be edited',
            type: 'error'
          });
        });
    }

  /**
   * Requests card modification to service, providing
   * modified Card object.
   * @return {Promise<Object>}
   */
  editCard() {
    return this.cardsService.editCard(this.card)
      .then(() => {
      this.$state.go('dashboard.cards.list');
    });
  }

  /**
   * Requests cards to service with filter object provided,
   * synchronizes pagination metadata on success.
   * @return {Promise<Object>}
   */
  applyCardsFilter() {
    return this.cardsService.getCards(this.filter)
      .then( () => {
        this.pagination.pageSize = this.cardsService.pageSize;
        this.pagination.total = this.cardsService.total;
        this.cards = this.cardsService.cards;
      });
  }

  /**
   * Gets currently logged user
   * @returns {Object|null}
   */
  getUser() {
    return this.authService.getUser();
  }

  /**
   * Reloads view in order to reset/clear filter parameters
   */
  reload(){
    this.$state.reload();
  }

  /**
   * Using moment library, formats date string into 'MMMM YYYY' format
   * @param {string} date Date in string format
   * @return {string}
   */
  static formatExpirationDateForMarkup(date){
    return moment(date).format('MMMM YYYY');
  }
}

import cardsController from './cards.controller';
import cardsService from './cards.service';

import baseTemplate from '../core/base.html';
import cardsAddTemplate from './views/add-card.html';
import cardsEditTemplate from './views/edit-card.html';
import cardsViewTemplate from './views/view-card.html';
import cardsTemplate from './cards.html';
import cardUsageModule from './card-usage/card-usage.js';

/** @ngInject */
const cardsModule = angular.module('app.cards', [cardUsageModule])
  .config(function ($stateProvider) {
    $stateProvider
      .state('dashboard.cards', {
        abstract: true,
        url: '/cards',
        template: baseTemplate,
      })
      .state('dashboard.cards.list', {
        url: '/list',
        template: cardsTemplate,
        controller: 'CardsCtrl as ctrl',
        resolve: {
          departments: (DepartmentsService) => DepartmentsService.getDepartments(),
          nonPaginatedUsers: (UsersService) =>
            UsersService.getNonPaginatedUsers(),

          //TODO: Fix: only pull users from backend is user is an administrator
          // users: ($q, CardsService, UsersService) => {
          //   let deferred = $q.defer();
          //   if(UsersService.getUser().user_type_name === 'admin')
          //     deferred.resolve();
          //   else {
          //     deferred.reject();
          //   }
          //   return deferred.promise;
          // }
        },
        cards: (CardsService) =>
          CardsService.getCards({status: 'Active'}),
      })
      .state('dashboard.cards.add', {
        url: '/add',
        template: cardsAddTemplate,
        controller: 'CardsCtrl as ctrl',
        resolve: {
          departments: (DepartmentsService) => DepartmentsService.getDepartments(),
          users: (UsersService) => UsersService.getUsers(),
          nonPaginatedUsers: (UsersService) =>
            UsersService.getNonPaginatedUsers(),
        },
      })
      .state('dashboard.cards.view', {
        url: '/:id',
        abstract: true,
        template: cardsViewTemplate,
        controller: 'CardsCtrl as ctrl',
        resolve: {
          card: ($stateParams, CardsService) => CardsService.getCard($stateParams.id),
        },
      })
      .state('dashboard.cards.edit', {
        url: '/:id/edit',
        template: cardsEditTemplate,
        controller: 'CardsCtrl as ctrl',
        resolve: {
          card: ($stateParams, CardsService) => CardsService.getCard($stateParams.id),
          departments: (DepartmentsService) => DepartmentsService.getDepartments(),
          users: (UsersService) => UsersService.getUsers(),
          nonPaginatedUsers: (UsersService) =>
            UsersService.getNonPaginatedUsers(),
        },
      });
  })
  .service('CardsService', cardsService)
  .controller('CardsCtrl', cardsController)
  .name;

/**
 * Cards is in charge of encapsulating all functionality
 * related to credit cards inside the application.
 * This angular module declares all states from the cards sub tree
 * of views and requires the CardsUsage module to function properly.
 * @type {string}
 * @return {string} 'app.cards'
 */
export default cardsModule;

/**
 * Cards Service is in charge of emitting http requests to the API
 * related to credit card information.
 */
export default class CardsService {
  /**
   * Constructs a new instance of CardsService and initializes it.
   * @param $http
   * @param $log
   * @param API
   */
  constructor($http, $log, API) {
    this.$http = $http;
    this.$log = $log;
    this.API = API;
    this.resource = '/api/v1/cards/';

    this.card = {};
    this.cards = [];

    // Initialize pagination metadata
    this.pageSize = 10;
    this.total = 1;
  }

  /**
   * Requests cards to the API, filtering results if params provided.
   * @param {Object} params Object where each key and value is used
   * for filtering requested Card objects
   * @return {Promise<Object>}
   */
  getCards(params) {
    return this.$http.get(this.API + this.resource, {
      params: params
    }).then((response) => {
        this.cards = response.data.data[0].data;
        this.pageSize = response.data.data[0].per_page;
        this.total = response.data.data[0].last_page;
        return this.cards;
      });
  }

  /**
   * Requests a single card to the API.
   * @param {number} id Numerical value provided by the API
   * @return {Promise<Object>}
   */
  getCard(id) {
    return this.$http.get(this.API + this.resource + id)
      .then((response) => {
        this.card = response.data.data;
        return this.card;
      });
  }

  /**
   * Requests the creation of a new Card object to the API.
   * @param {Object} card Card for creation
   * @return {Promise<Object>}
   */
  createCard(card) {
    return this.$http.post(this.API + this.resource, card);
  };

  /**
   * Requests the deletion of a single existing Card object to the API.
   * @param {Object} card Card for deletion
   * @returns {Promise<Object>}
   */
  deleteCard(card) {
    card.status = 'Inactive';
    return this.$http.put(this.API + this.resource + card.id, card);
  };

  /**
   * Requests the modification of a single existing
   * Card object to the API.
   * @param {Object} card Card for modification
   * @returns {Promise<Object>}
   */
  editCard(card) {
    return this.$http.put(this.API + this.resource + card.id, card);
  };
}

/**
 * AuthController is in charge of handling user input at the
 * login portal.
 */
export default class AuthController {
  /**
   * Constructs a new instance of AuthController and initializes it.
   * @param $state
   * @param $log
   * @param AuthService
   */
  constructor ($state, $log, AuthService, swal) {
    // angular injected services
    this.$state = $state;
    this.$log = $log;
    this.authService = AuthService;
    this.swal = swal;

    // Temporary user object for form
    this.user = {};
  }

  /**
   * Login function that calls AuthService with user credentials.
   * @return {Promise<Object>}
   */
  login() {
    return this.authService.authenticate(this.user)
      .then((user) => {
        this.user = {};
        if(user.user_type_name === 'vehicle_admin'){
          this.$state.go('dashboard.vehicles.list');
        }
        else{
          this.$state.go('dashboard.home');
        }
      })
      .catch((error) => {
        this.$log.log(error);

        if(error.data === null){
          this.swal({
            title: 'Error',
            text: 'Something went wrong! Please try again later.',
            type: 'error',
          });
        }
        else if(error.data !== null && error.data.error === 'invalid_credentials'){
          this.swal({
            title: 'Error',
            text: 'Invalid credentials. Please, enter a valid email and password.',
            type: 'error',
          });
        }
        else {
          this.swal({
            title: 'Error',
            text: 'Something went wrong! Please try again later.',
            type: 'error',
          });
        }
      });
  }

  showForgotPasswordDialog(){
    this.swal({
      title: 'Forgot Password?',
      type: 'info',
      text: 'Please contact RUM Vehicle\'s administrator at (787) 832-4040 ext. 2020/2024 in order to ' +
      'reset your password. Location: Jos De Diego\'s Building, Office 205.'
    });

  }
}

import authController from './auth.controller';
import authService from './auth.service';
import loginTemplate from './views/login.html';

/** @ngInject */
const authModule = angular.module('core.auth', [
  'ui.router', 'ngStorage'])
  // Config block for state declaration
  .config(function ($stateProvider, $urlRouterProvider) {
    $stateProvider
      // Login state
      .state('login', {
        url: '/login',
        parent: 'base',
        template: loginTemplate,
        controller: 'AuthCtrl as ctrl',
        resolve: {
          redirectAuthorized: ($q, $state, $timeout, AuthService) => {
            let deferred = $q.defer();
            if(!AuthService.isLoggedIn())
              deferred.resolve();
            else {
              $timeout(() => $state.go('dashboard.home'));
              deferred.reject();
            }
            return deferred.promise.catch(() => {});
          }
        }
      });

      $urlRouterProvider.when('/', '/login');
      $urlRouterProvider.when('', '/login');
  })
  // Declare module elements
  .service('AuthService', authService)
  .controller('AuthCtrl', authController)
  // Return module name string for portability
  .name;

/**
 * Auth is in charge of encapsulating all functionality
 * related to authentication and security measures inside the application.
 * This angular module declares all states from the login portal.
 * @type {string}
 * @return {string} 'core.auth'
 */
export default authModule;

/**
 * AuthService is in charge of enforcing security
 * measures in the core application layer by emitting http
 * requests to the API for authenticating users into the app.
 */
export default class AuthService {
  /**
   * Constructs a new instance of AuthService and initializes it.
   * @param $http
   * @param $log
   * @param $sessionStorage
   * @param API
   */
  constructor($http, $log, $sessionStorage, API) {
    this.$http = $http;
    this.$log = $log;
    this.$sessionStorage = $sessionStorage;
    this.API = API;

    if(this.$sessionStorage.token){
      this.$http.defaults.headers.common.Authorization = 'Bearer ' + this.$sessionStorage.token;
    }
    else {
      this.$http.defaults.headers.common.Authorization = 'Basic';
      this.$sessionStorage.user = {};
    }

  }

  /**
   * Authenticates a user with credentials and
   * returns its details.
   * @param {Object} user credentials
   * @return {Promise<Object>}
   */
  authenticate(user) {
    return this.$http.post(this.API + '/api/v1/auth', user)
      .then((response) => {
        this.$sessionStorage.token = response.data.token;
        this.$http.defaults.headers.common.Authorization = 'Bearer ' + this.$sessionStorage.token;
          return this.currentUser();
      });
  }


  /**
   * Provides all user object details of current logged in user.
   * @return {Promise<Object>}
   */
  currentUser() {
    return this.$http.get(this.API + '/api/v1/auth/me')
      .then((response) => {
        this.$sessionStorage.user = response.data.data;
        return this.$sessionStorage.user;
      });
  }

  /**
   * Logs user out and removes all credentials from service scope.
   */
  logOut() {
    this.$http.defaults.headers.common.Authorization = 'Basic';
    delete this.$sessionStorage.token;
    delete this.$sessionStorage.user;
  }

  /**
   * Get current token property.
   * @return {Object|null}
   */
  getToken() {
    if (this.isLoggedIn()) {
      return this.$sessionStorage.token;
    }

    return null;
  }

  /**
   * Get current user property.
   * @return {Object|null}
   */
  getUser() {
    if (this.isLoggedIn()) {
      return this.$sessionStorage.user;
    }

    return null;
  }

  /**
   * Checks for token existence.
   * @return {boolean}
   * @private
   */
  isLoggedIn() {
    return !!this.$sessionStorage.token;
  }
}

import chai from 'chai';
import authModule from './auth';

chai.should();

describe('AuthModule', function() {
  beforeEach(function() {
    window.module(authModule);
  });

  describe('AuthService', function() {
    let service;
    let $httpBackend;

    beforeEach(function() {
      inject(function($injector) {
        service = $injector.get('AuthService');
        $httpBackend = $injector.get('$httpBackend');
      });
    });

    it('should have function for logging out users', function() {
      service.should.have.property('logOut').that.is.a('function');
    });

    it('should have function for checking current user', function() {
      service.should.have.property('getUser').that.is.a('function');
    });

    it('should give authorized users their data', function(done) {
      // Mock a user with credentials
      let mockedUser = {email: 'test@upr.edu', password: 'SECRET'};
      let mockedToken = {token: 'SECRET'};
      let mockedDetails = {data: 'SECRET'};
      // Expect service to POST with user credentials
      // Mock 200 response with token
      $httpBackend
        .expect('POST', 'http://dev.uprm.edu/rumvehicles/api/v1/auth', mockedUser)
        .respond(200, mockedToken);
      // Expect service to GET user details with token
      // Mock 200 response with user data
      $httpBackend
        .expect('GET', 'http://dev.uprm.edu/rumvehicles/api/v1/auth/me')
        .respond(200, mockedDetails);

      // Execute authenticate with mocked input
