/**
 * Users is in charge of encapsulating all functionality
 * related to users inside the application.
 * @type {string}
 * @return {string} 'app.users'
 */
export default usersModule;

/**
 * Users Service is in charge of emitting http requests to the API
 * related to user information.
 */
export default class UsersService {
  /**
   * Constructs a new instance of UsersService and initializes it.
   * @param $http
   * @param $log
   * @param API
   */
  constructor($http, $log, API) {
    this.$http = $http;
    this.$log = $log;
    this.API = API;
    this.resource = '/api/v1/custodians/';

    this.user = {};
    this.users = [];
    this.nonPaginatedUsers = [];
    this.userTypes = [];

    // Initialize pagination metadata
    this.total = 1;
    this.page = 1;
    this.pageSize = 10;
  }

  /**
   * Requests users to the API, filtering results if params provided.
   * @param {Object} params Object where each key and value is used
   * for filtering requested User objects
   * @return {Promise<Object>}
   */
  getUsers(params) {
    return this.$http.get(this.API + this.resource, {
      params: params
    }).then((response) => {
        this.users = response.data.data[0].data;
        this.pageSize = response.data.data[0].per_page;
        this.total = response.data.data[0].total;
        return this.users;
      });
  }

  /**
   * Requests a single user to the API.
   * @param {number} id Numerical value provided by the API
   * @return {Promise<Object>}
   */
  getUser(id) {
    return this.$http.get(this.API + this.resource + id)
      .then((response) => {
        this.user = response.data.data;
        return this.user;
      });
  }

  /**
   * Requests the creation of a new User object to the API.
   * @param {Object} user User for creation
   * @return {Promise<Object>}
   */
  createUser(user) {
    return this.$http.post(this.API + this.resource, user);
  }

  /**
   * Requests the deletion of a single existing User object to the API.
   * @param {Object} user User for deletion
   * @returns {Promise<Object>}
   */
  deleteUser(user) {
    user.user_type_id = 4;  //4 is for Inactive
    return this.$http.put(this.API + this.resource + user.id, user);
  }

  /**
   * Requests the modification of a single existing
   * User object to the API.
   * @param {Object} user User for modification
   * @returns {Promise<Object>}
   */
  editUser(user) {
    return this.$http.put(this.API + this.resource + user.id, user);
  }

  /**
   * Requests user types to the API, in a list sorted by name.
   * @return {Promise<Object[]>}
   */
  getUserTypes() {
    return this.$http.get(this.API +'/api/v1/user-types')
      .then((response) => {
        this.userTypes = response.data;
        return this.userTypes;
      });
  }

  /**
   * Requests users to the API, in a list sorted by name.
   * @return {Promise<Object[]>}
   */
  getNonPaginatedUsers() {
    return this.$http.get(this.API + '/api/v1/custodians-list')
      .then((response) => {
        this.nonPaginatedUsers = response.data;
        return this.nonPaginatedUsers;
      });
  }
}

import moment from 'moment-es6';

/**
 * Vehicles Controller is in charge of all business logic related to UPRM's Vehicles
 */
export default class VehiclesController {
  constructor($state, $log,$window,
           AuthService, DepartmentsService, UsersService, VehiclesService, swal) {
    this.$state = $state;
    this.$log = $log;
    this.$window = $window;
    this.authService = AuthService;
    this.departmentsService = DepartmentsService;
    this.usersService = UsersService;
    this.vehiclesService = VehiclesService;

    this.vehicles = this.vehiclesService.vehicles;
    this.vehicle = this.vehiclesService.vehicle;
    this.departments = this.departmentsService.departments;
    this.users = this.usersService.users;
    this.vehicleTypes = this.vehiclesService.vehicleTypes;
    this.newVehicle = {};
    this.swal = swal;

    this.date = new Date();

    this.maxVehicleYearAllowed = new Date(
      this.date.getFullYear() + 1,
      this.date.getMonth(),
      this.date.getDate()
    );

    this.maxMarbeteDate = new Date(
      this.date.getFullYear() + 1,
      this.date.getMonth() + 1,
      this.date.getDate()
    );

    this.today = new Date(
      this.date.getFullYear(),
      this.date.getMonth(),
      this.date.getDate()
    );

    // Filter object will contain filter parameters for filtering vehicles
    this.filter = {
      department_id: '',
      custodian_id: '',
      type_id: '',
      page: 1,
      was_archived: 0
    };

    this.pagination = {
      pageSize: this.vehiclesService.pageSize,
      total: this.vehiclesService.total,
    };

    //If user actually selected a date, then format it
    if(this.vehicle.marbete_date) {
      this.vehicle.marbete_date = moment(this.vehicle.marbete_date).toDate();
    }

    if(this.vehicle.inspection_date){
      this.vehicle.inspection_date = moment(this.vehicle.inspection_date).toDate();
    }

    if(this.vehicle.decomission_date){
      this.vehicle.decomission_date = moment(this.vehicle.decomission_date).toDate();
    }

    if(this.vehicle.inscription_date){
      this.vehicle.inscription_date = moment(this.vehicle.inscription_date).toDate();
    }
  }

  /**
   * If vehicle creation is successful, shows user success feedback
   * If vehicle creation is unsuccessful, shows user error feedback
   */
  confirmVehicleCreation(){

    if(parseInt(this.newVehicle.year) > parseInt(this.maxVehicleYearAllowed.getFullYear())){
      this.swal({
        title: 'Error',
        text: ' Vehicle year can\'t be greater than '+this.maxVehicleYearAllowed.getFullYear()+" and the year entered was "+this.newVehicle.year+"." ,
        type: 'error'
      });
      return;
    }
    this.createVehicle()
      .then(() => {
        this.swal({
          title: 'Vehicle was successfully created!',
          type: 'success'
        });
      })
      .catch((error) => {
        this.swal({
          title: 'Error: Vehicle could not be created',
          type: 'error'
        });
      });
  }

  /**
   * Sends vehicle to be created to the Vehicles Service
   * Formats input dates as YYYY-MM-DD
   */
  createVehicle() {

    this.newVehicle.was_archived = 0;
    this.newVehicle.custodian_id = this.authService.getUser().id;

    //If user actually selected a date, then format it
    if(this.newVehicle.marbete_date) {
      this.newVehicle.marbete_date = moment(this.newVehicle.marbete_date).format('YYYY-MM-DD');
    }

    if(this.newVehicle.inspection_date){
      this.newVehicle.inspection_date = moment(this.newVehicle.inspection_date).format('YYYY-MM-DD');
    }

    if(this.newVehicle.decomission_date){
      this.newVehicle.decomission_date = moment(this.newVehicle.decomission_date).format('YYYY-MM-DD');
    }

    if(this.newVehicle.inscription_date){
      this.newVehicle.inscription_date = moment(this.newVehicle.inscription_date).format('YYYY-MM-DD');
    }

    return this.vehiclesService.createVehicle(this.newVehicle)
      .then(() => {
      this.$state.go('dashboard.vehicles.list');
      });
  }

  /**
   *  Shows confirmation dialog to user
   * If user confirms, vehicle will be deleted (if no error in deletion is present)
   * If user cancels, he/she will stay in the "Vehicles" view
   * @param vehicle Vehicle to be deleted
   */
  confirmVehicleDeletion(vehicle) {

    this.swal({
      title: 'Do you really want to delete this vehicle?',
      type: 'warning',
      confirmButtonText: 'Confirm',
      confirmButtonColor: '#4caf50',
      showCancelButton: 'true',
      cancelButtonText: 'Cancel',
      cancelButtonColor: '#f44336',
    }).then(() => {

      this.deleteVehicle(vehicle)
        .then(() => {

          this.swal({
            title: 'Vehicle has been deleted',
            type: 'success'
          });

          this.$state.reload();
      })
        .catch((error) => {
          this.swal({
            title: 'Error: Vehicle could not be deleted',
            type: 'error'
          });
        });
    });
  }

  /**
   *Sends vehicle to be deleted to the Vehicles Service
   * @param vehicle Vehicle to be deleted
   * @returns {*}
   */
  deleteVehicle(vehicle) {
    return this.vehiclesService.deleteVehicle(vehicle);
  }

  /**
   * If vehicle edition is successful, shows user success feedback
   * If vehicle edition is unsuccessful, shows user error feedback
   */
  confirmVehicleEdition(){

    if(parseInt(this.vehicle.year) > parseInt(this.maxVehicleYearAllowed.getFullYear())) {
      this.swal({
        title: 'Error',
        text: ' Vehicle year can\'t be greater than ' + this.maxVehicleYearAllowed.getFullYear() + " and the year entered was " + this.vehicle.year + ".",
        type: 'error'
      });
      return;
    }
      this.editVehicle()
        .then(() => {
          this.swal({
            title: 'Vehicle was successfully edited!',
            type: 'success'
          });
        })
        .catch((error) => {
          this.swal({
            title: 'Error: Vehicle could not be edited',
            type: 'error'
          });
        });
    }

  /**
   * Sends vehicle to be edited to the Vehicles Service
   */
  editVehicle() {

    //If user actually selected a date, then format it
    if(this.vehicle.marbete_date) {
      this.vehicle.marbete_date = moment(this.vehicle.marbete_date).format('YYYY-MM-DD');
    }

    if(this.vehicle.inspection_date){
      this.vehicle.inspection_date = moment(this.vehicle.inspection_date).format('YYYY-MM-DD');
    }

    if(this.vehicle.decomission_date){
      this.vehicle.decomission_date = moment(this.vehicle.decomission_date).format('YYYY-MM-DD');
    }

    if(this.vehicle.inscription_date){
      this.vehicle.inscription_date = moment(this.vehicle.inscription_date).format('YYYY-MM-DD');
    }

    return this.vehiclesService.editVehicle(this.vehicle)
      .then(() => {
      this.$state.go('dashboard.vehicles.list');
      });
  }

  /**
   * gets current system User
   * @returns {*|Object} User Object
   */
  getUser() {
    return this.authService.getUser();
  }

  /**
   * Requests Vehicles Service a list of vehicles with filter parameters applied
   */
  applyVehicleFilter() {
    return this.vehiclesService.getVehicles(this.filter)
      .then(() => {
        this.pagination.pageSize = this.vehiclesService.pageSize;
        this.pagination.total = this.vehiclesService.total;
        this.vehicles = this.vehiclesService.vehicles;
      });
  }

  /**
   * Reloads current state. Used for clearing filter parameters applied
   */
  reload(){
    this.$state.reload();
  }

  /**
   * @param date Date to be formatted
   * @returns {string} returns a String with the following format: MMM DD, YYYY
   */
  formatDateForMarkup(date){
    return moment(date).format('MMM DD, YYYY');
  }

  /**
   * Downloads an excel file which includes all vehicles in the system
   */
  downloadVehicleReport(){
    this.$window.open('http://dev.uprm.edu/rumvehicles/api/v1/dashboard/report/vehicles', "_self");
  }
}

import vehiclesController from './vehicles.controller';
import vehiclesService from './vehicles.service';

import baseTemplate from '../core/base.html';
import vehiclesTemplate from './vehicles.html';
import vehiclesAddTemplate from './views/add-vehicle.html';
import vehiclesEditTemplate from './views/edit-vehicle.html';
import vehiclesViewTemplate from './views/view-vehicle.html';

/** @ngInject */
const vehiclesModule = angular.module('app.vehicles', [])
  .config(function($stateProvider) {
    $stateProvider
      .state('dashboard.vehicles', {
        abstract: true,
        url: '/vehicles',
        template: baseTemplate,
      })
      .state('dashboard.vehicles.list', {
        url: '/list',
        template: vehiclesTemplate,
        controller: 'VehiclesCtrl as ctrl',
        resolve: {
          vehicles: (VehiclesService) =>
            VehiclesService.getVehicles({was_archived: 0}),
          departments: (DepartmentsService) =>
            DepartmentsService.getDepartments(),
          users: (UsersService) =>
            UsersService.getUsers(),
          vehicleTypes: (VehiclesService) =>
            VehiclesService.getVehicleTypes()
        },
      })
      .state('dashboard.vehicles.add', {
        url: '/add',
        template: vehiclesAddTemplate,
        controller: 'VehiclesCtrl as ctrl',
        resolve: {
          departments: (DepartmentsService) =>
            DepartmentsService.getDepartments(),
          vehicleTypes: (VehiclesService) =>
            VehiclesService.getVehicleTypes()
        },
      })
      .state('dashboard.vehicles.view', {
        url: '/:id',
        template: vehiclesViewTemplate,
        controller: 'VehiclesCtrl as ctrl',
        resolve: {
          vehicle: ($stateParams, VehiclesService) =>
            VehiclesService.getVehicle($stateParams.id)
        },
      })
      .state('dashboard.vehicles.edit', {
        url: '/:id/edit',
        template: vehiclesEditTemplate,
        controller: 'VehiclesCtrl as ctrl',
        resolve: {
          vehicle: ($stateParams, VehiclesService) =>
            VehiclesService.getVehicle($stateParams.id),
          departments: (DepartmentsService) =>
            DepartmentsService.getDepartments(),
          vehicleTypes: (VehiclesService) =>
            VehiclesService.getVehicleTypes()
        },
      });
  })
  .service('VehiclesService', vehiclesService)
  .controller('VehiclesCtrl', vehiclesController)
  .name;

/**
 * Vehicles is in charge of encapsulating all functionality
 * related to vehicles inside the application.
 * This angular module declares all states from
 * the vehicles sub tree of views.
 * @type {string}
 * @return {string} 'app.vehicles'
 */
export default vehiclesModule;

/**
 * Vehicles Service is in charge of API calls (GET, POST, PUT, DELETE)
 * related to UPRM's Vehicles
 */
export default class VehiclesService {

  constructor($http, $log, API) {
    this.$http = $http;
    this.$log = $log;
    this.API = API;
    this.resource = '/api/v1/vehicles/';

    this.vehicle = {};
    this.vehicles = [];
    this.vehicleTypes = [];

    // Initialize pagination metadata
    this.pageSize = 10;
    this.total = 1;
  }

  /**
   * Requests all vehicles if no param is given
   * If filtering params are given, then requests vehicles that apply only
   * @param params Filtering parameters for vehicles
   */
  getVehicles(params = {}) {
    return this.$http.get(this.API + this.resource, {
      params: params
    }).then((response) => {
        this.vehicles = response.data.data[0].data;
        this.pageSize = response.data.data[0].per_page;
        this.total = response.data.data[0].last_page;
        return this.vehicles;
      });
  }

  /**
   * Requests the backend for a specific vehicle
   * @param id Vehicle ID
   */
  getVehicle(id) {
    return this.$http.get(this.API + this.resource + id)
      .then((response) => {
        this.vehicle = response.data.data;
        return this.vehicle;
      });
  }

  /**
   * Requests backend to create a new vehicle
   * @param vehicle Vehicle Object
   */
  createVehicle(vehicle) {
    return this.$http.post(this.API + this.resource, vehicle);
  }

  /**
   * Deletes a specific vehicle from the backend
   * @param vehicle Vehicle Object
   * @returns {Promise} Server response. If delete was not successful, catch error and log it.
   */
  deleteVehicle(vehicle) {
    vehicle.was_archived = 1;
    return this.$http.put(this.API + this.resource + vehicle.id, vehicle);
  }

  /**
   * Modifies a vehicle in the backend
   * @param vehicle Vehicle to be modified
   * @returns {FinishedRequest<T>} Server response. If edit was not successful, catch error and log it.
   */
  editVehicle(vehicle) {
    return this.$http.put(this.API + this.resource + vehicle.id, vehicle);
  }

  /**
   * Gets vehicle types from backend. E.g. Golf car, Mini-Van, Gas Car, etc.
   */
  getVehicleTypes() {
    return this.$http.get(this.API + '/api/v1/vehicle-types')
        .then((response) => {
      this.vehicleTypes = response.data;
          return this.vehicleTypes;
    })
      .catch((error) => {
        this.$log.log(error);
      });
  }

}
